def map_letters(raw):
	as_map = {}
	row=col=0
	for row,line in enumerate(raw.split("\n")):
		line = line.strip()
		for col,char in enumerate(line):
			as_map[row,col] = char
	return as_map

def search_pos(as_map, row, col):
	checks = [
		"".join([as_map.get( (row, col+i),"?") for i in range(4) ]),
		"".join([as_map.get( (row, col-i),"?") for i in range(4) ]),
		"".join([as_map.get( (row+i, col),"?") for i in range(4) ]),
		"".join([as_map.get( (row-i, col),"?") for i in range(4) ]),

		"".join([as_map.get( (row+i, col+i),"?") for i in range(4) ]),
		"".join([as_map.get( (row+i, col-i),"?") for i in range(4) ]),
		"".join([as_map.get( (row-i, col+i),"?") for i in range(4) ]),
		"".join([as_map.get( (row-i, col-i),"?") for i in range(4) ]),
	]
	return len( [s for s in checks if s == "XMAS"] )

def main(puzzle_input):
	as_map = map_letters(puzzle_input)
	count = 0
	for key,val in as_map.items():
		row, col = key
		count += search_pos(as_map, row, col)
		
	print(count)

	


